html,
body {
  padding: 0;
  margin: 0;
  height: 100%;
}
/* entire app is wrapped in a div that must have height for flexbox to work */
body > div:first-of-type {
  height: 100%;
}
.app {
  display: flex;
  flex-direction: column;
  font-family: "Open Sans";
  height: 100%;
}
.app_header {
  background-color: #0d1626;
  height: 52px;
}
.app_header,
.app_header a {
  color: #eee;
}
.app_logo {
  width: 25px;
  vertical-align: middle;
  margin: -4px 10px 0;
}
.app_nav {
  list-style: none;
  margin: 0 10px;
  padding: 0;
  display: inline-block;
}
.app_nav li {
  color: #eee;
  font-weight: bold;
  display: inline-block;
  cursor: pointer;
}
.app_nav li a {
  color: #eee;
  text-decoration: none;
  padding: 10px 20px 7px;
  display: inline-block;
  border-bottom: 3px solid transparent;
}
.app_nav li a.active,
.app_nav li a:hover {
  border-bottom-color: #6EAEFF;
  color: #6EAEFF;
}
.app_main {
  display: flex;
  flex: 1 1 auto;
  min-height: 0;
}
.posts {
  display: flex;
  flex-direction: row;
  width: 100%;
}
.posts_list {
  background-color: #F7FAFF;
  list-style: none;
  padding: 0;
  margin: 0;
  min-width: 400px;
  max-width: 400px;
  overflow-y: auto;
}
.posts_post {
  padding: 15px 20px;
  color: #333;
  cursor: pointer;
  position: relative;
  transition: background-color 0.3s ease;
}
.posts_post:hover {
  background-color: #ECF3FF;
}
.posts_post--selected {
  background-color: #DFEBFF;
}
.posts_post--selected:hover {
  background-color: #DFEBFF;
}
.posts_post--draft {
  color: #aaa;
  font-style: italic;
}
.posts_post-date {
  position: absolute;
  line-height: 1;
  color: #999;
  bottom: 5px;
  right: 5px;
  font-size: 10px;
}
.posts_display {
  overflow-y: auto;
  flex: 1 1 auto;
}
.posts_content {
  overflow: auto;
  padding: 30px;
}
.posts_draft-message {
  background-color: #DFEBFF;
  padding: 5px;
  text-align: center;
  color: white;
  font-style: italic;
  font-weight: bold;
  opacity: .9;
}
.icon-link {
  opacity: 0;
  position: absolute;
  padding: 5px 6px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 14px;
  line-height: 1;
  transition: opacity 0.3s ease, background-color 0.1s ease;
}
.icon-link:hover {
  background-color: white;
}
.posts_post:hover .icon-link {
  opacity: 1;
}
.posts_edit-link {
  top: 5px;
  right: 5px;
  opacity: 0;
  position: absolute;
  padding: 5px 6px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 14px;
  line-height: 1;
  transition: opacity 0.3s ease, background-color 0.1s ease;
}
.posts_edit-link:hover {
  background-color: white;
}
.posts_post:hover .posts_edit-link {
  opacity: 1;
}
.posts_perma-link {
  top: 5px;
  right: 30px;
  opacity: 0;
  position: absolute;
  padding: 5px 6px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 14px;
  line-height: 1;
  transition: opacity 0.3s ease, background-color 0.1s ease;
}
.posts_perma-link:hover {
  background-color: white;
}
.posts_post:hover .posts_perma-link {
  opacity: 1;
}
.editor {
  display: flex;
  flex-direction: column;
  min-height: 0;
  width: 100%;
}
.editor_top {
  position: relative;
}
.editor_title {
  width: 100%;
  border: none;
  outline: none;
  box-sizing: border-box;
  padding: 10px 25px;
  font-size: 34px;
  font-weight: bold;
}
.editor--draft .editor_title {
  font-style: italic;
  color: #aaa;
}
.pb-button {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
}
.pb-button:hover {
  opacity: 1;
}
.editor_publish {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
  color: #ffd5d5;
  background-color: #da1212;
  text-shadow: 1px 1px #7c3131;
}
.editor_publish:hover {
  opacity: 1;
}
.editor_unpublish {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
  padding: 7px 5px 7px 0;
  color: gray;
}
.editor_unpublish:hover {
  opacity: 1;
}
.editor_remove {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
  padding: 8px 4px 7px 0;
  opacity: 1;
  color: black;
  top: 14px;
  right: 175px;
}
.editor_remove:hover {
  opacity: 1;
}
.editor_checkGrammar {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
  padding: 7px 5px 7px 0;
  opacity: 1;
  color: black;
  top: 14px;
  right: 215px;
}
.editor_checkGrammar:hover {
  opacity: 1;
}
.editor_updated {
  float: right;
  font-size: 14px;
  font-family: Inconsolata;
  color: #aaa;
}
.editor_word-count {
  float: right;
  font-size: 14px;
  font-family: Inconsolata;
  color: #aaa;
}
.editor_perma-link {
  text-decoration: none;
  position: relative;
  top: 1px;
  left: 5px;
  color: #aaa;
  transition: color .3s ease;
}
.editor_perma-link:hover {
  color: black;
}
.editor_main {
  display: flex;
  flex: 1 1 auto;
  min-height: 0;
  flex-direction: row;
  padding: 5px 0 0;
  background-color: #B0FFD0;
  position: relative;
}
.editor_main > div {
  background-color: white;
}
.editor_no-content {
  margin-top: 30px;
  font-size: 30px;
  color: #ddd;
  font-style: italic;
}
.code-mirror_wrappper {
  flex: 1;
}
@font-face {
