/* DEFAULT THEME */
.CodeMirror {
  font-family: monospace;
  height: 300px;
  color: black;
  position: relative;
  overflow: hidden;
  background: white;
}
.CodeMirror pre {
  padding: 0 4px;
  -moz-border-radius: 0;
  -webkit-border-radius: 0;
  border-radius: 0;
  border-width: 0;
  background: transparent;
  font-family: inherit;
  font-size: inherit;
  margin: 0;
  white-space: pre;
  word-wrap: normal;
  line-height: inherit;
  color: inherit;
  z-index: 2;
  position: relative;
  overflow: visible;
  -webkit-tap-highlight-color: transparent;
  -webkit-font-variant-ligatures: none;
  font-variant-ligatures: none;
}
.CodeMirror div.CodeMirror-secondarycursor {
  border-left: 1px solid silver;
}
.CodeMirror-lines {
  padding: 4px 0;
  cursor: text;
  min-height: 1px;
}
.CodeMirror-scrollbar-filler {
  background-color: white;
  position: absolute;
  z-index: 6;
  display: none;
  right: 0;
  bottom: 0;
}
.CodeMirror-gutter-filler {
  background-color: white;
  position: absolute;
  z-index: 6;
  display: none;
  left: 0;
  bottom: 0;
}
.CodeMirror-gutters {
  border-right: 1px solid #dddddd;
  background-color: #f7f7f7;
  white-space: nowrap;
  position: absolute;
  left: 0;
  top: 0;
  min-height: 100%;
  z-index: 3;
  -moz-box-sizing: content-box;
  box-sizing: content-box;
}
.CodeMirror-linenumber {
  padding: 0 3px 0 5px;
  min-width: 20px;
  text-align: right;
  color: #999999;
  white-space: nowrap;
  -moz-box-sizing: content-box;
  box-sizing: content-box;
}
.CodeMirror-guttermarker {
  color: black;
}
.CodeMirror-guttermarker-subtle {
  color: #999999;
}
.CodeMirror-cursor {
  border-left: 1px solid black;
  border-right: none;
  width: 0;
  position: absolute;
  pointer-events: none;
}
.cm-fat-cursor .CodeMirror-cursor {
  width: auto;
  border: 0 !important;
  background: #77ee77;
}
.cm-fat-cursor div.CodeMirror-cursors {
  z-index: 1;
}
.cm-animate-fat-cursor {
  width: auto;
  border: 0;
  -webkit-animation: blink 1.06s steps(1) infinite;
  -moz-animation: blink 1.06s steps(1) infinite;
  animation: blink 1.06s steps(1) infinite;
  background-color: #77ee77;
}
.cm-tab {
  display: inline-block;
