  background-clip: padding-box;
  font-size: .8em;
  outline: 0;
}
.modal-backdrop {
  background-color: #000;
  height: 100%;
  left: 0;
  position: absolute;
  right: 0;
  top: 0;
  width: 100%;
  z-index: 10;
}
.modal-backdrop.fade {
  opacity: 0;
  -webkit-transition: opacity 0.15s linear;
  -o-transition: opacity 0.15s linear;
  transition: opacity 0.15s linear;
}
.modal-backdrop.in {
  opacity: 0.8;
