.app_main {
  display: flex;
  flex: 1 1 auto;
  min-height: 0;
}
.posts {
  display: flex;
  flex-direction: row;
  width: 100%;
}
.posts_list {
  background-color: #F7FAFF;
  list-style: none;
  padding: 0;
  margin: 0;
  min-width: 400px;
  max-width: 400px;
  overflow-y: auto;
}
.posts_post {
  padding: 15px 20px;
  color: #333;
  cursor: pointer;
  position: relative;
  transition: background-color 0.3s ease;
}
.posts_post:hover {
  background-color: #ECF3FF;
}
.posts_post--selected {
  background-color: #DFEBFF;
}
.posts_post--selected:hover {
  background-color: #DFEBFF;
}
.posts_post--draft {
  color: #aaa;
  font-style: italic;
}
.posts_post-date {
  position: absolute;
  line-height: 1;
  color: #999;
  bottom: 5px;
  right: 5px;
  font-size: 10px;
}
.posts_display {
  overflow-y: auto;
  flex: 1 1 auto;
}
.posts_content {
  overflow: auto;
  padding: 30px;
}
.posts_draft-message {
  background-color: #DFEBFF;
  padding: 5px;
  text-align: center;
  color: white;
  font-style: italic;
  font-weight: bold;
  opacity: .9;
}
.icon-link {
  opacity: 0;
  position: absolute;
  padding: 5px 6px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 14px;
  line-height: 1;
  transition: opacity 0.3s ease, background-color 0.1s ease;
}
.icon-link:hover {
  background-color: white;
}
.posts_post:hover .icon-link {
  opacity: 1;
}
.posts_edit-link {
  top: 5px;
  right: 5px;
  opacity: 0;
  position: absolute;
  padding: 5px 6px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 14px;
  line-height: 1;
  transition: opacity 0.3s ease, background-color 0.1s ease;
}
.posts_edit-link:hover {
  background-color: white;
}
.posts_post:hover .posts_edit-link {
  opacity: 1;
}
.posts_perma-link {
  top: 5px;
  right: 30px;
  opacity: 0;
  position: absolute;
  padding: 5px 6px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 14px;
  line-height: 1;
  transition: opacity 0.3s ease, background-color 0.1s ease;
}
.posts_perma-link:hover {
  background-color: white;
}
.posts_post:hover .posts_perma-link {
  opacity: 1;
}
