.editor_publish {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
