.editor_unpublish {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
  padding: 7px 5px 7px 0;
  color: gray;
}
.editor_unpublish:hover {
  opacity: 1;
}
.editor_remove {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
  padding: 8px 4px 7px 0;
  opacity: 1;
