  top: 14px;
  right: 175px;
}
.editor_remove:hover {
  opacity: 1;
}
.editor_checkGrammar {
  outline: none;
  border: transparent;
  background-color: transparent;
  position: absolute;
  right: 14px;
  top: 15px;
  font-size: 18px;
  cursor: pointer;
  padding: 7px 20px;
  border-radius: 5px;
  font-weight: bold;
  opacity: .5;
  transition: opacity .3s ease;
  padding: 7px 5px 7px 0;
  opacity: 1;
  color: black;
  top: 14px;
  right: 215px;
}
.editor_checkGrammar:hover {
  opacity: 1;
}
.editor_updated {
